import {
    FETCH_NEWS_START,
    FETCH_NEWS_SUCCESS,
    FETCH_NEWS_FAILURE
} from 'actionsType'

import {fetchNews as fetchNewsApi} from 'api'

export const fetchNews = () => async dispatch => {
    dispatch({type: FETCH_NEWS_START})

    try {
        const body = await fetchNewsApi()

        dispatch({
            type: FETCH_NEWS_SUCCESS,
            payload: body
        })
    } catch (err) {
        dispatch({
            type: FETCH_NEWS_FAILURE,
            payload: err,
            error: true
        })
    }
}
