import {
    FETCH_LOGIN_START,
    FETCH_LOGIN_SUCCESS,
    FETCH_LOGIN_FAILURE
} from 'actionsType'

import {fetchLogin as fetchLoginApi} from 'api'

export const fetchLogin =(login,pass)=>async dispatch =>{
    dispatch({type: FETCH_LOGIN_START})
    try{
        const resLogin = await fetchLoginApi(login,pass)

        dispatch({
            type: FETCH_LOGIN_SUCCESS,
            payload: resLogin
        })
    }
    catch(err) {
        dispatch({
            type: FETCH_LOGIN_FAILURE,
            payload: {
                status: 'err',
                message: err
            }
        })
    }

}